package com.hefshine.myass.controlstatement;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String fruit="Apple";
		switch(fruit)
		{
		  case "Mango": System.out.println("Fruit is Mango");
		       break;
		       
		  case "Apple":System.out.println("Fruit is Apple");
		       break;
		      
		  case "Strawbery":System.out.println("Fruit is Strawbery");
	       break;
	       
		  default:System.out.println("Fruit not present in list");
		}
	}

}
