package com.hefshine.myass.controlstatement;

public class UpperLower {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='g';
		if(ch>=65 && ch<=90)
		{
			System.out.println("Upper Case");
		}
		if(ch>=97 && ch<=122)
		{
			System.out.println("Lower Case");
		}	
	}

}
