package com.hefshine.myass.iteration;

public class Que1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int j=1,k=1;
		for(int i=1;i<=100;i++)
		{
			System.out.print(i+" ");
		}
		System.out.println();
		while(j<=100)
		{
			System.out.print(j+" ");
			j++;
		}
		System.out.println();
		do{
			System.out.print(k+" ");
			k++;	
		}while(k<=100);
	}

}
