package com.hefshine.myass.iteration;

public class Que7 {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int even=0,odd=0;
		for(int i=1;i<=100;i++)
		{
			if(i%2==0)
			{
				even=i;
				System.out.print("Even Number"+even+" ");
				System.out.println();
			}
		
			else
			{
				odd=i;
				System.out.print("Odd Number"+odd+" ");
				
			}
			
			
		}

		
		
	}

}
